pragma solidity ^0.8.22;

import "../Leaderboard/Leaderboard.sol";
import { SwResolver } from "../SwResolver.sol";
import { AccessControlUpgradeable } from "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import { PausableUpgradeable } from "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import { UUPSUpgradeable } from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

contract Crash is 
    UUPSUpgradeable,
    AccessControlUpgradeable,
    PausableUpgradeable,
    SwResolver {
    // -----------
    /// Structs
    // -----------
    struct BetInfo {
        address user;
        uint256 betAmount;
        uint256 multiplier;
    }

    // -----------
    /// Constants
    // -----------
    bytes32 private constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    uint256 public constant GAME_ID = 2;

    // -----------
    /// Storages
    // -----------
    uint256 public totalGame;
    address public leaderboard;
    mapping(uint256 => BetInfo) public gameIdToBetInfo;

    // -----------
    // Events
    // -----------
    event NewBet(address indexed user, uint256 indexed gameId, uint256 betAmount, uint256 multiplier, uint256 fee);
    event Game(address indexed user, uint256 indexed gameId, uint256 betAmount, uint256 multiplier, uint256 payout);

    function initialize(address leaderboard_, address switchboard_, bytes32 swQueue_) public initializer {
        __Context_init_unchained();
        __AccessControl_init_unchained();
        __Pausable_init_unchained();
        __UUPSUpgradeable_init();

        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _grantRole(ADMIN_ROLE, _msgSender());

        leaderboard = leaderboard_;
        setupResolver(switchboard_, swQueue_);
    }

    function _calculateBetArray(
        uint256 multiplier
    ) public pure returns (uint256 winRate, uint256 totalRate) {
        // Extract the fractional part by multiplying the multiplier by 100 and getting the remainder
        uint256 fraction = multiplier % 100;

        // Determine the number of repetitions based on the fractional part
        if (fraction == 25) {
            winRate = 4;
        } else if (fraction == 50) {
            winRate = 2;
        } else if (fraction == 75) {
            winRate = 4; // Needs 4 repetitions to sum to a whole number
        } else {
            winRate = 1; // Whole numbers and zero fraction
        }

        // Calculate the total sum when the multiplier is used 'repeatMultiplier' times
        uint256 totalSum = multiplier * winRate;

        // Calculate the total number of elements needed (rounded up to ensure whole number)
        totalRate = (totalSum + 99) / 100; // Ceiling equivalent for total sum
    }

    // multiplier: 100 -> 10000 (x1 -> x100)
    function crash(uint256 multiplier) external payable whenNotPaused {
        // Validate must divisable to 25
        require(
            multiplier > 100 && multiplier < 10000 && multiplier % 25 == 0,
            "invalid cashOut"
        );

        address sender = _msgSender();
        // take fee
        (uint256 _betAmount, uint256 fee) = Leaderboard(leaderboard).takeFee{
            value: msg.value
        }();

        // push bet info
        uint256 gameId = totalGame + 1;
        requestRandomNumber(gameId);
        BetInfo memory betInfo = BetInfo({
            user: sender,
            betAmount: _betAmount,
            multiplier: multiplier
        });
        gameIdToBetInfo[gameId] = betInfo;
        totalGame = gameId;

        // New Bet
        emit NewBet(sender, gameId, _betAmount, multiplier, fee);
    }

    // handle entropy callback
    function handleRandomNumber(
        uint256 gameId,
        uint256 randomNumber
    ) internal override whenNotPaused {
        BetInfo storage betInfo = gameIdToBetInfo[gameId];
        uint256 multiplier = betInfo.multiplier;
        uint256 betAmount = betInfo.betAmount;
        address user = betInfo.user;

        if (
            betAmount == 0
        ) {
            // The game is ended
            return;
        }
        // Set bet amount to zero to prevent re-entrancy
        betInfo.betAmount = 0;

        /// calculate rate
        (uint256 winRate, uint256 totalRate) = _calculateBetArray(multiplier);

        // check result
        uint256 rand = randomNumber % totalRate;

        bool isWin = rand < winRate;

        // check if win
        uint256 _rewardAmount = betAmount * multiplier;
        if (!isWin) {
            _rewardAmount = 0;
        }

        Leaderboard(leaderboard).earnReward(
            GAME_ID,
            user,
            _rewardAmount,
            betAmount
        );
        emit Game(user, gameId, betAmount, multiplier, _rewardAmount);


    }

    function setLeaderboard(address leaderboard_) public onlyRole(ADMIN_ROLE) {
        leaderboard = leaderboard_;
    }

    /**
     * @dev called by the owner to pause, triggers stopped state
     */
    function pause() external onlyRole(ADMIN_ROLE) whenNotPaused {
        _pause();
    }

    /**
     * @dev called by the owner to unpause, returns to normal state
     */
    function unpause() external onlyRole(ADMIN_ROLE) whenPaused {
        _unpause();
    }

    receive() external payable {}

    function getRandomUint() internal view returns (uint256) {
        return
            uint256(
                keccak256(abi.encodePacked(block.number, totalGame, msg.sender))
            );
    }

    function _authorizeUpgrade(address) internal override onlyRole(ADMIN_ROLE) {}
}
","keccak256":"0x24f377b82da1189bdb261d1aaafda1bc57a830150c66b7bf5c2326f102be9084","license":"MIT"},"contracts/Leaderboard/Leaderboard.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import { AccessControlUpgradeable } from "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import { UUPSUpgradeable } from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

contract Leaderboard is 
    UUPSUpgradeable,
    AccessControlUpgradeable {
    bytes32 private constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 private constant GAME_ROLE = keccak256("GAME_ROLE");
    bytes32 private constant WITHDRAWER = keccak256("WITHDRAWER");
    uint256 private constant FEE_DENOMINATOR = 10_000;

    //////////////
    /// Storage
    //////////////
    mapping(address => uint256) public points;
    mapping(address => uint256) public pendingReward;
    uint256 public betFee;
    uint256 public minBet;
    uint256 public maxBet;
    address public feeReceiver;
    uint256 public govFee;
    address public govAddress;

    //////////////
    /// Events
    //////////////
    event NewPoint(
        uint256 gameId,
        address user,
        uint256 earnAmount,
        uint256 betAmount
    );
    event Withdraw(address user, uint256 amount);
    event Deposit(address user, uint256 amount);
    event ClaimReward(address user, uint256 amount);

    //////////////
    /// Constructor
    //////////////
    function initialize() public initializer {
        __Context_init_unchained();
        __AccessControl_init_unchained();
        __UUPSUpgradeable_init();

        address sender = _msgSender();

        _grantRole(DEFAULT_ADMIN_ROLE, sender);
        _grantRole(ADMIN_ROLE, sender);
        _grantRole(GAME_ROLE, sender);
        _grantRole(WITHDRAWER, sender);

        betFee = 350;
        minBet = 25 ether / 100_000; // 0.00025
        maxBet = 2 ether / 100; // 0.02
        govFee = 1 ether / 1_000_000; // 0.000001

        feeReceiver = sender;
        govAddress = sender;
    }

    function takeFee() public payable returns (uint256 betAmount, uint256 fee) {
        uint256 _betAmountBeforeFee = msg.value - govFee;
        betAmount =
            (_betAmountBeforeFee * FEE_DENOMINATOR) /
            (FEE_DENOMINATOR + betFee);
        require(betAmount >= minBet && betAmount <= maxBet, "invalid bet");

        fee = _betAmountBeforeFee - betAmount;

        payable(govAddress).transfer(govFee);
        payable(feeReceiver).transfer(fee);
    }

    function takeFeeWithAttemp(uint256 attempts) public payable returns (uint256 betAmount, uint256 fee) {
        uint256 _betAmountBeforeFee = msg.value - govFee;
        betAmount =
            (_betAmountBeforeFee * FEE_DENOMINATOR) /
            (FEE_DENOMINATOR + betFee);
        require(betAmount >= minBet*attempts && betAmount <= maxBet*attempts, "invalid bet");

        fee = _betAmountBeforeFee - betAmount;

        payable(govAddress).transfer(govFee);
        payable(feeReceiver).transfer(fee);
    }

    function setBetFee(uint256 val) public onlyRole(ADMIN_ROLE) {
        betFee = val;
    }

    function setMinBet(uint256 val) public onlyRole(ADMIN_ROLE) {
        require(val <= maxBet, "invalid bet");
        minBet = val;
    }

    function setMaxBet(uint256 val) public onlyRole(ADMIN_ROLE) {
        require(val >= minBet, "invalid bet");
        maxBet = val;
    }

    function setGovFee(uint256 val) public onlyRole(ADMIN_ROLE) {
        govFee = val;
    }

    function setFeeReceiver(address receiver) public onlyRole(ADMIN_ROLE) {
        feeReceiver = receiver;
    }

    function setGovAddress(address gov) public onlyRole(ADMIN_ROLE) {
        govAddress = gov;
    }

    function withdrawAll(address addr) external onlyRole(WITHDRAWER) {
        address payable _to = payable(addr);
        _to.transfer(address(this).balance);
        emit Withdraw(msg.sender, address(this).balance);
    }

    function withdraw(
        address addr,
        uint256 amount
    ) external onlyRole(WITHDRAWER) {
        uint256 balance = address(this).balance;
        require(amount <= balance, "invalid amount");
        address payable _to = payable(addr);
        _to.transfer(amount);
        emit Withdraw(msg.sender, amount);
    }

    function earnReward(
        uint256 gameId,
        address user,
        uint256 earnAmount,
        uint256 betAmount
    ) external onlyRole(GAME_ROLE) {
        if (earnAmount > 0) {
            pendingReward[user] += earnAmount;
            if (earnAmount > betAmount) {
                points[user] += (earnAmount - betAmount);
                emit NewPoint(gameId, user, earnAmount, betAmount);
            }
        }
    }

    function claimReward() external {
        uint256 earnAmount = pendingReward[_msgSender()];
        require(earnAmount > 0, "no reward");
        pendingReward[_msgSender()] = 0;
        payable(_msgSender()).transfer(earnAmount);

        emit ClaimReward(_msgSender(), earnAmount);
    }

    function deposit() public payable {
        emit Deposit(msg.sender, msg.value);
    }

    function _authorizeUpgrade(address) internal override onlyRole(ADMIN_ROLE) {}
}
","keccak256":"0x370b88ecc8892318f537b94c14281de9f2d0b2cd987f20df33cb13b7e4bc4823","license":"MIT"},"contracts/SwResolver.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import {Structs} from "@switchboard-xyz/on-demand-solidity/structs/Structs.sol";
import {ISwitchboard} from "@switchboard-xyz/on-demand-solidity/ISwitchboard.sol";
import { Initializable } from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

abstract contract SwResolver is Initializable {
    address private _switchboard;
    bytes32 public queue;
    uint256 public sequence;
    uint256 public solvedSeq;
    mapping(uint256 => uint256) public seq2GameId;

    event RandomRequested(uint256 sequence);

    // https://docs.switchboard.xyz/product-documentation/randomness/tutorials/evm
    function setupResolver(address switchboard_, bytes32 swQueue_) internal initializer {
        queue = swQueue_;
        _switchboard = switchboard_;
    }

    function requestRandomNumber(uint256 gameId) internal {
        require(seq2GameId[gameId] == 0, "Game exists");

        sequence += 1;
        ISwitchboard(_switchboard).requestRandomness(
            bytes32(sequence),        // randomnessId (bytes32): Unique ID for the request.
            address(this),            // authority (address):  Only this contract should manage randomness. 
            queue,                    // queueId (bytes32 ): Chain selection for requesting randomness.
            1                         // minSettlementDelay (uint16): Minimum seconds to settle the request.
        );

        // Store the number to identify the callback request
        seq2GameId[sequence] = gameId;

        emit RandomRequested(sequence);
    }

    function resolve(
        bytes[] calldata switchboardUpdateFeeds,
        uint256 targetSeq
    ) external {
        uint256 tagetGameId = seq2GameId[targetSeq];

        require(targetSeq > solvedSeq && tagetGameId != 0, "invalid seq");
        uint256 fromSeq = solvedSeq;
        solvedSeq = targetSeq;

        // invoke
        ISwitchboard(_switchboard).updateFeeds(switchboardUpdateFeeds);

        for (uint256 _seq = fromSeq + 1; _seq <= targetSeq; _seq++) {
            Structs.RandomnessResult memory randomness = ISwitchboard(_switchboard).getRandomness(bytes32(_seq)).result;
            require(randomness.settledAt != 0, "Randomness failed to Settle");

            uint256 gameId = seq2GameId[_seq];
            handleRandomNumber(gameId, randomness.value);
        }
    }

    function handleRandomNumber(
        uint256 gameId,
        uint256 randomNumber
    ) internal virtual;
}","keccak256":"0x72d2bf6b2ff06ff96b06f9bf64c37055c7c8b368ae5a9725614abfa5d16ab525","license":"MIT"}},"version":1}
