//SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "../Leaderboard/Leaderboard.sol";

contract Mines is AccessControl, Pausable {
    // -----------
    /// Constants
    // -----------
    bytes32 private constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    uint256 private constant FEE_DENOMINATOR = 10_000;
    uint256 public constant GRID_SIZE = 16;
    uint256 public constant GAME_ID = 4;

    // -----------
    /// Storages
    // -----------
    uint256 public totalGame;
    uint256 public betFee;
    uint256 public minBet;
    uint256 public maxBet;
    address public feeReceiver;
    uint256 public govFee;
    address public govAddress;
    address public leaderboard;

    // -----------
    // Events
    // -----------
    event Game(
        uint256 betAmount,
        uint256 fee,
        address user,
        uint256 reward,
        uint256 multiplier
    );

    constructor(address leaderboard_) {
        address sender = _msgSender();

        _grantRole(DEFAULT_ADMIN_ROLE, sender);
        _grantRole(ADMIN_ROLE, sender);

        betFee = 350;
        minBet = 25 ether / 10_000; // 0.0025
        maxBet = 2 ether / 10; // 0.2
        govFee = 1 ether / 1_000_000; // 0.00001

        feeReceiver = sender;
        govAddress = sender;
        leaderboard = leaderboard_;
    }

    function _calculateBetArray(
        uint256 mines,
        uint256 level
    )
        public
        pure
        returns (uint256 winRate, uint256 totalRate, uint256 multiplier)
    {
        totalRate = GRID_SIZE - level;
        winRate = totalRate - mines;
        multiplier = uint256((totalRate * 100) / (totalRate - mines));
    }

    function checkMine(
        uint256 mines,
        uint256 level
    ) external payable whenNotPaused {
        require(
            mines == 1 ||
                mines == 3 ||
                mines == 5 ||
                mines == 10 ||
                mines == 15,
            "invalid mines"
        );
        require(mines <= GRID_SIZE - level, "Mines exceed remaining cells");

        address sender = _msgSender();
        // take fee
        uint256 _betAmountBeforeFee = msg.value - govFee;
        uint256 _betAmount = (_betAmountBeforeFee * FEE_DENOMINATOR) /
            (FEE_DENOMINATOR + betFee);
        require(_betAmount >= minBet && _betAmount <= maxBet, "invalid bet");

        uint256 fee = _betAmountBeforeFee - _betAmount;
        totalGame = totalGame + 1;

        payable(govAddress).transfer(govFee);
        payable(feeReceiver).transfer(fee);

        (
            uint256 winRate,
            uint256 totalRate,
            uint256 multiplier
        ) = _calculateBetArray(mines, level);

        uint256 _rewardAmount = (multiplier * _betAmount) / 100;
        require(address(this).balance >= _rewardAmount, "house out of balance");

        // check result
        uint256 rand = getRandomUint();
        rand = rand % totalRate;

        bool isWin = rand < winRate;
        uint256 earnAmount = 0;
        if (isWin) {
            payable(sender).transfer(_rewardAmount);
            if (_rewardAmount > msg.value) {
                earnAmount = _rewardAmount - msg.value;
            }
        } else {
            _rewardAmount = 0;
            multiplier = 0;
        }
        Leaderboard(leaderboard).newPoint(
            GAME_ID,
            msg.sender,
            earnAmount,
            _betAmount
        );

        emit Game(_betAmount, fee, sender, _rewardAmount, multiplier);
    }

    function setBetFee(uint256 val) public onlyRole(ADMIN_ROLE) {
        betFee = val;
    }

    function setMinBet(uint256 val) public onlyRole(ADMIN_ROLE) {
        require(val <= maxBet, "invalid bet");
        minBet = val;
    }

    function setMaxBet(uint256 val) public onlyRole(ADMIN_ROLE) {
        require(val >= minBet, "invalid bet");
        maxBet = val;
    }

    function setGovFee(uint256 val) public onlyRole(ADMIN_ROLE) {
        govFee = val;
    }

    function setFeeReceiver(address receiver) public onlyRole(ADMIN_ROLE) {
        feeReceiver = receiver;
    }

    function setGovAddress(address gov) public onlyRole(ADMIN_ROLE) {
        govAddress = gov;
    }

    function withdrawAll(address addr) external onlyRole(ADMIN_ROLE) {
        address payable _to = payable(addr);
        _to.transfer(address(this).balance);
    }

    function withdraw(
        address addr,
        uint256 amount
    ) external onlyRole(ADMIN_ROLE) {
        uint256 balance = address(this).balance;
        require(amount <= balance, "invalid amount");
        address payable _to = payable(addr);
        _to.transfer(amount);
    }

    function setLeaderboard(address leaderboard_) public onlyRole(ADMIN_ROLE) {
        leaderboard = leaderboard_;
    }

    /**
     * @dev called by the owner to pause, triggers stopped state
     */
    function pause() external onlyRole(ADMIN_ROLE) whenNotPaused {
        _pause();
    }

    /**
     * @dev called by the owner to unpause, returns to normal state
     */
    function unpause() external onlyRole(ADMIN_ROLE) whenPaused {
        _unpause();
    }

    receive() external payable {}

    function getRandomUint() internal view returns (uint256) {
        return
            uint256(
                keccak256(
                    abi.encodePacked(
                        block.timestamp,
                        block.prevrandao,
                        totalGame,
                        msg.sender
                    )
                )
            );
    }
}

contracts/Plinko/Plinko.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "../Leaderboard/Leaderboard.sol";
import "hardhat/console.sol";

contract Plinko is AccessControl, Pausable {
    // -----------
    /// Constants
    // -----------
    bytes32 private constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    uint256 private constant FEE_DENOMINATOR = 10_000;
    uint256 private constant DEGEN_LENGTH = 49;
    uint256 private constant BASIC_LENGTH = 66;
    uint256 private constant MAX_MULTIPLIER = 1500;
    uint256 public constant GAME_ID = 3;

    // -----------
    /// Storages
    // -----------
    uint256 public totalGame;
    uint256 public betFee;
    uint256 public minBet;
    uint256 public maxBet;
    address public feeReceiver;
    uint256 public govFee;
    address public govAddress;
    address public leaderboard;
    mapping(uint256 => uint256) public degenMultiplier;
    mapping(uint256 => uint256) public basicMultiplier;

    // -----------
    // Events
    // -----------
    event Game(
        uint256 betAmount,
        uint256 fee,
        address user,
        uint256 multiplier,
        uint256 rewardAmount
    );

    constructor(address leaderboard_) {
        address sender = _msgSender();

        _grantRole(DEFAULT_ADMIN_ROLE, sender);
        _grantRole(ADMIN_ROLE, sender);

        betFee = 350;
        minBet = 25 ether / 10_000; // 0.0025
        maxBet = 2 ether / 10; // 0.2
        govFee = 1 ether / 1_000_000; // 0.000001

        feeReceiver = sender;
        govAddress = sender;
        leaderboard = leaderboard_;

        // Degen
        degenMultiplier[43] = 200;
        degenMultiplier[44] = 200;
        degenMultiplier[45] = 1000;
        degenMultiplier[46] = 1000;
        degenMultiplier[47] = 1000;
        degenMultiplier[48] = 1500;

        // Basic
        for (uint256 i = 0; i < 48; i++) basicMultiplier[i] = 50;
        for (uint256 i = 48; i < 58; i++) basicMultiplier[i] = 150;
        for (uint256 i = 58; i < 65; i++) basicMultiplier[i] = 300;
        basicMultiplier[65] = 600;
    }

    function _calculateBetArray(
        uint256 multiplier
    ) public pure returns (uint256 winRate, uint256 totalRate) {
        // Extract the fractional part by multiplying the multiplier by 100 and getting the remainder
        uint256 fraction = multiplier % 100;

        // Determine the number of repetitions based on the fractional part
        if (fraction == 25) {
            winRate = 4;
        } else if (fraction == 50) {
            winRate = 2;
        } else if (fraction == 75) {
            winRate = 4; // Needs 4 repetitions to sum to a whole number
        } else {
            winRate = 1; // Whole numbers and zero fraction
        }

        // Calculate the total sum when the multiplier is used 'repeatMultiplier' times
        uint256 totalSum = multiplier * winRate;

        // Calculate the total number of elements needed (rounded up to ensure whole number)
        totalRate = (totalSum + 99) / 100; // Ceiling equivalent for total sum
    }

    function plinko(bool degen, uint256 ball) external payable whenNotPaused {
        address sender = _msgSender();
        // take fee
        // 2597500000000000n - (1n * 10000000000000n)
        uint256 _betAmountBeforeFee = msg.value - (ball * govFee); // 2587500000000000n
        // (2587500000000000n * 10000n) / (10000n + 350n)
        uint256 _betAmount = (_betAmountBeforeFee * FEE_DENOMINATOR) /
            (FEE_DENOMINATOR + betFee) /
            ball; // 2500000000000000n
        require(_betAmount >= minBet && _betAmount <= maxBet, "invalid bet");

        uint256 fee = _betAmountBeforeFee - (_betAmount * ball); // 87500000000000n
        totalGame += ball;

        payable(govAddress).transfer(govFee);
        payable(feeReceiver).transfer(fee);

        // 1500n * 2500000000000000n / 100n
        uint256 maxRewardAmount = (ball * (MAX_MULTIPLIER * _betAmount)) / 100; // 37500000000000000n
        require(
            address(this).balance >= maxRewardAmount,
            "house out of balance"
        );

        uint256 totalRate = degen ? DEGEN_LENGTH : BASIC_LENGTH;

        console.log("maxRewardAmount: %s", maxRewardAmount);

        // check result
        uint totalRewardAmount = 0;
        uint256 rand = getRandomUint();
        console.log("Rand: %s", rand);

        for (uint256 i = 0; i < ball; i++) {
            uint256 randIndex = (rand * (i + 1)) % totalRate;
            console.log("randIndex: %s", randIndex);

            uint256 multiplier = degen
                ? degenMultiplier[randIndex]
                : basicMultiplier[randIndex];
            uint rewardAmount = (multiplier * _betAmount) / 100;
            console.log("Reward Amount: %s", rewardAmount);

            totalRewardAmount += rewardAmount;
            emit Game(_betAmount, fee, sender, multiplier, rewardAmount);
        }

        if (totalRewardAmount > 0) {
            payable(sender).transfer(totalRewardAmount);
        }

        uint earnAmount = 0;
        if (totalRewardAmount > msg.value) {
            earnAmount = totalRewardAmount - msg.value;
        }

        Leaderboard(leaderboard).newPoint(
            GAME_ID,
            msg.sender,
            earnAmount,
            _betAmount * ball
        );
    }

    function setBetFee(uint256 val) public onlyRole(ADMIN_ROLE) {
        betFee = val;
    }

    function setMinBet(uint256 val) public onlyRole(ADMIN_ROLE) {
        require(val <= maxBet, "invalid bet");
        minBet = val;
    }

    function setMaxBet(uint256 val) public onlyRole(ADMIN_ROLE) {
        require(val >= minBet, "invalid bet");
        maxBet = val;
    }

    function setGovFee(uint256 val) public onlyRole(ADMIN_ROLE) {
        govFee = val;
    }

    function setFeeReceiver(address receiver) public onlyRole(ADMIN_ROLE) {
        feeReceiver = receiver;
    }

    function setGovAddress(address gov) public onlyRole(ADMIN_ROLE) {
        govAddress = gov;
    }

    function withdrawAll(address addr) external onlyRole(ADMIN_ROLE) {
        address payable _to = payable(addr);
        _to.transfer(address(this).balance);
    }

    function withdraw(
        address addr,
        uint256 amount
    ) external onlyRole(ADMIN_ROLE) {
        uint256 balance = address(this).balance;
        require(amount <= balance, "invalid amount");
        address payable _to = payable(addr);
        _to.transfer(amount);
    }

    function setLeaderboard(address leaderboard_) public onlyRole(ADMIN_ROLE) {
        leaderboard = leaderboard_;
    }

    /**
     * @dev called by the owner to pause, triggers stopped state
     */
    function pause() external onlyRole(ADMIN_ROLE) whenNotPaused {
        _pause();
    }

    /**
     * @dev called by the owner to unpause, returns to normal state
     */
    function unpause() external onlyRole(ADMIN_ROLE) whenPaused {
        _unpause();
    }

    receive() external payable {}

    function getRandomUint() internal view returns (uint256) {
        return
            uint256(
                keccak256(
                    abi.encodePacked(
                        block.timestamp,
                        block.prevrandao,
                        totalGame,
                        msg.sender
                    )
                )
            );
    }
}

